import React, {useState} from "react";

import './ExpenseForm.css';
import Card from '../Card';

const ExpenseForm = () => {
  const [item,setItem] = useState("");
  const [amount,setAmount] = useState("");
  const [date,setDate] = useState("");

  const onChangeItemHandler = (event) => {
    setItem(() => event.target.value);
  };
  const onChangeAmountHandler = (event) => {
    setAmount(() => event.target.value);
  };const onChangeDateHandler = (event) => {
    setDate(() => event.target.value);
  };

  

  return (
    <Card className="main-form">
      <div className="form-section">
        <label>Item:</label>
        <input type="text" onChange = {onChangeItemHandler}/>
      </div>

      <div className="form-section">
        <label>Amount</label>
        <input type="number" min="0.01" step="0.01" onChange = {onChangeAmountHandler} />
      </div>

      <div className="form-section">
        <label>Date</label>
        <input type="date" min="2019-01-01" max="2022-01-01" onChange = {onChangeDateHandler}/>
      </div>
    </Card>
  );
};

export default ExpenseForm;
