import React, {useState} from "react";

import './ExpenseForm.css';
import Card from '../Card';

const ExpenseForm = (props) => {
  const [title,setTitle] = useState("");
  const [amount,setAmount] = useState("");
  const [date,setDate] = useState("");

  const onChangeTitleHandler = (event) => {
    setTitle(() => event.target.value);
  };
  const onChangeAmountHandler = (event) => {
    setAmount(() => event.target.value);
  };const onChangeDateHandler = (event) => {
    setDate(() => event.target.value);
  };

  const expense = {
    titleExpense: title,
    amountExpense: amount,
    dateExpense: date
  };

  props.addItemInArray(expense);

  return (
    <Card className="main-form">
      <div className="form-section">
        <label>Title:</label>
        <input type="text" onChange = {onChangeTitleHandler}/>
      </div>

      <div className="form-section">
        <label>Amount:</label>
        <input type="number" min="0.01" step="0.01" onChange = {onChangeAmountHandler} />
      </div>

      <div className="form-section">
        <label>Date:</label>
        <input type="date" min="2019-01-01" max="2022-01-01" onChange = {onChangeDateHandler}/>
      </div>
    </Card>
  );
};

export default ExpenseForm;
